const filter=()=>{let t=document.querySelectorAll(".filter"),c=document.querySelectorAll(".content-product");t.forEach(e=>{t.forEach((e,t)=>{0===t?e.classList.add("active"):e.classList.remove("active")}),e.addEventListener("click",()=>{t.forEach(e=>{e.classList.remove("active")}),c.forEach(e=>{e.classList.add("d-none")}),e.classList.add("active"),document.getElementById(e.dataset.type).classList.remove("d-none")})})};export{filter};